import "./App.css";
import './style.css';
import serv1 from "./images/soft1.png";
import serv2 from "./images/soft2.png";
import serv3 from "./images/soft3.png";
import SideMenu, { menuItems } from "./components/SideMenu";
import LogIn from "./components/login.component";

import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import { useState } from "react";

// const Dashboard = () => <h1>Dashboard</h1>

const Dashboard = () => <div>
<section className="services services-search">
        {/* <div className="Center-set"> */}
          <div className="search-center-controller">
            <button className="search-center-btn">
              <i class="bi bi-search"></i>
            </button>

            <input type="text" placeholder="search" />
          </div>
        {/* </div> */}
      </section>

<section className="services">
            {/* <h1 className="heading"> what we offer </h1> */}

            <div className="box-container">

                <div className="box">
                    {/* <img src="images/serv-1.png" alt="" /> */}
                    <img src={serv1} alt="" />
                    <h3>VLC</h3>
                    <p>Media Player</p>
                    <a href="#" className="btn">Install</a>
                </div>

                <div className="box">
                    {/* <img src="images/serv-2.png" alt="" /> */}
                    <img src={serv2} alt="" />

                    <h3>MS Office</h3>
                    <p>Office Management</p>
                    <a href="#" className="btn">Install</a>
                </div>

                <div className="box">
                    {/* <img src="images/serv-3.png" alt="" /> */}
                    <img src={serv3} alt="" />

                    <h3>Coral Draw</h3>
                    <p>Media Player</p>
                    <a href="#" className="btn">Install</a>
                </div>

            </div>

        </section></div>;



const Content = () => <h1>Content</h1>;
const Courses = () => <h1>Content/Courses</h1>;
const Videos = () => <h1>Content/Videos</h1>;
const Design = () => <h1>Design</h1>;
const Content2 = () => <h1>Content2</h1>;
const Courses2 = () => <h1>Content/Courses 2</h1>;
const Videos2 = () => <h1>Content/Videos 2</h1>;
const Design2 = () => <h1>Design 2</h1>;

function App() {
  const [inactive, setInactive] = useState(false);

  return (
    <div className="App">
      <Router>
        <SideMenu
          onCollapse={(inactive) => {
            console.log(inactive);
            setInactive(inactive);
          }}
        />

        <div className={`container ${inactive ? "inactive" : ""}`}>
          {/* improvememt, not recorded in video, its just looping through menuItems
          instead of hard coding all the routes */}
          {menuItems.map((menu, index) => (
            <>
              <Route key={menu.name} exact={menu.exact} path={menu.to}>
                <h1>{menu.name}</h1>
              </Route>
              {menu.subMenus && menu.subMenus.length > 0
                ? menu.subMenus.map((subMenu, i) => (
                    <Route key={subMenu.name} path={subMenu.to}>
                      <h1>{subMenu.name}</h1>
                    </Route>
                  ))
                : null}
            </>
          ))}

          <Switch>
            {/* <Route exact path={"/"}>
              <LogIn />
            </Route> */}
            <Route exact path={"/"}>
              <Dashboard />
            </Route>
            <Route exact path={"/content"}>
              <Content />
            </Route>
            <Route path={"/content/courses"}>
              <Courses />
            </Route>
            <Route path={"/content/videos"}>
              <Videos />
            </Route>
            <Route path={"/design"}>
              <Design />
            </Route>
            <Route exact path={"/content-2"}>
              <Content2 />
            </Route>
            <Route path={"/content-2/courses"}>
              <Courses2 />
            </Route>
            <Route path={"/content-2/videos"}>
              <Videos2 />
            </Route>
            <Route path={"/design-2"}>
              <Design2 />
            </Route>
          </Switch>
        </div>
      </Router>
    </div>
  );
}

export default App;
